INSERT INTO `cms_article` VALUES ('77', '9', '运用高抛低吸做法，灵活操作', '', '', '', '', '<h1>\r\n	<p style=\"text-align:justify;background:white;\">\r\n		<span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;从技术层面上看，日线“赢在波段”指标呈现绿色，行情震荡偏空，但重重利空出尽，原油可能筑底反弹。建议运用高抛低吸做法，灵活操作。价格在</span><span style=\"font-size:16px;\">2300</span><span style=\"font-size:16px;\">附近做多，在</span><span style=\"font-size:16px;\">2370</span><span style=\"font-size:16px;\">附近做空。参考支撑位：</span><span style=\"font-size:16px;\">2295</span><span style=\"font-size:16px;\">，阻力位：</span><span style=\"font-size:16px;\">2370</span><span style=\"font-size:16px;\">。止损</span><span style=\"font-size:16px;\">30</span><span style=\"font-size:16px;\">点，灵活止盈（</span><span style=\"font-size:16px;\">30-70</span><span style=\"font-size:16px;\">点）。</span><span></span> \r\n	</p>\r\n	<p style=\"text-align:justify;background:white;\">\r\n		<span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;行情复杂多变，请做好止损，不要频繁做单。</span><span></span> \r\n	</p>\r\n<span></span> \r\n</h1>', '2015-12-22 16:04:56', '0', '0', '0', '1', '', '', '', 'edit', '0');
INSERT INTO `cms_article` VALUES ('78', '35', '投资前景', '', '', '', '', '<p>\r\n	<strong><span style=\"font-size:16px;color:#E53333;\">政府赋予契机，市场潜力无限</span></strong>\r\n</p>\r\n<p>\r\n	2009年11月\r\n</p>\r\n<p>\r\n	国务院--中国供销总社为落实《国务院关于加快供销合作社改革发展的若干意见》（国发〔2009）40号）文件要求、加快推进经营战略转型、在新形势下更好更深入地服务于实体经济而打造的全国性再生资源与金融服务交易平台。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;color:#E53333;\">全国唯一一家在清理整顿期间经部际联席会议通过的交易所</span></strong>\r\n</p>\r\n<p>\r\n	2013年5月\r\n</p>\r\n<p>\r\n	大连再生资源交易所通过全国清理整顿各类交易场所部际联席会议备案，成为获得监管部门认可、具备经营资质的交易所。它是全国唯一一家在清理整顿期间经部际联席会议验收通过，由交易市场直接升格为交易所的交易场所；它在全国参差不齐的交易平台中独树一帜！\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;color:#E53333;\">中国当前股市的低迷和交易规则</span></strong>\r\n</p>\r\n<p>\r\n	2015年虽股市一路飙涨到5000点，却依然显示中国股市的不健全。中国股市在许多投资百姓中是一辈子抹不去的伤痛。入金A股虽然有所成长，但交易机制仍然十分落后，这与我国国情和经济发展息息相关。原油投资是美国兴起的国际投资平台，而美国是世界上股市制度最健全的国家，相应的制度相对比较完善。\r\n</p>', '2015-12-22 16:22:15', '0', '0', '0', '1', '', '', '', 'edit', '0');
